
def motif(s, t):
    positions = []
    left = 0
    right = left + len(t)

    while right < len(s):
        if s[left : right] == t:
            positions.append(left + 1)
        left += 1
        right += 1

    return positions


s = '' 
t = 'GGCATGTGG'
print(motif(s, t))

