
def charwise_and(str1, str2):

    if len(str1) != len(str2):
        raise ValueError("Strings must be of the same length")
    
    mutations = 0
    for char1, char2 in zip(str1, str2):
        if char1 != char2:
            mutations += 1

    return mutations

dna1 = ''
dna2 = ''
print(charwise_and(dna1, dna2))